import React, { useState } from 'react';
import './Explore.css';

function Explore() {
  const [activeTab, setActiveTab] = useState('paris');

  const places = {
    paris: {
      name: 'Paris',
      description: 'The city of lights and love, known for the Eiffel Tower and exquisite cuisine.',
    },
    maldives: {
      name: 'Maldives',
      description: 'A tropical paradise with crystal-clear waters and white sandy beaches.',
    },
    tokyo: {
      name: 'Tokyo',
      description: 'A bustling metropolis blending traditional culture with modern technology.',
    },
  };

  return (
    <section className="explore">
      <h2>Explore</h2>
      <div className="tabs">
        {Object.keys(places).map((key) => (
          <button
            key={key}
            className={`tab ${activeTab === key ? 'active' : ''}`}
            onClick={() => setActiveTab(key)}
          >
            {places[key].name}
          </button>
        ))}
      </div>
      <div className="tab-content">
        <h3>{places[activeTab].name}</h3>
        <p>{places[activeTab].description}</p>
      </div>
    </section>
  );
}

export default Explore;
