import React, { useState } from 'react';
import './Explore.css';

function Explore() {
  const [activeTab, setActiveTab] = useState('paris');

  const places = {
    paris: {
      name: 'Paris',
      description: 'The city of lights and love, known for the Eiffel Tower and exquisite cuisine.',
      background: 'https://images.unsplash.com/photo-1431274172761-fca41d930114?w=600&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MTZ8fHBhcmlzJTIwd2FsbHBhcGVyfGVufDB8fDB8fHww',
    },
    maldives: {
      name: 'Maldives',
      description: 'A tropical paradise with crystal-clear waters and white sandy beaches.',
      background: 'https://images.unsplash.com/photo-1507525428034-b723cf961d3e?w=600&auto=format&fit=crop&q=60',
    },
    tokyo: {
      name: 'Tokyo',
      description: 'A bustling metropolis blending traditional culture with modern technology.',
      background: 'https://images.unsplash.com/photo-1549692520-acc6669e2f0c?w=600&auto=format&fit=crop&q=60',
    },
  };

  return (
    <section
      className="explore"
      style={{ backgroundImage: `url(${places[activeTab].background})` }}
    >
      <h2>Explore</h2>
      <div className="tabs">
        {Object.keys(places).map((key) => (
          <button
            key={key}
            className={`tab ${activeTab === key ? 'active' : ''}`}
            onClick={() => setActiveTab(key)}
          >
            {places[key].name}
          </button>
        ))}
      </div>
      <div className="tab-content">
        <h3>{places[activeTab].name}</h3>
        <p>{places[activeTab].description}</p>
      </div>
    </section>
  );
}

export default Explore;
