import React, { useState } from 'react';
import './Explore.css';

function Explore() {
  const [activeTab, setActiveTab] = useState('tajmahal');

  const places = {
    tajmahal: {
      name: 'Taj Mahal',
      description: 'An iconic symbol of love, located in Agra, and one of the Seven Wonders of the World.',
      background: 'https://plus.unsplash.com/premium_photo-1661902184762-49c2c8ebb787?w=600&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8OXx8dGFqJTIwbWFoYWwlMjBhZ3JhJTIwaW5kaWF8ZW58MHx8MHx8fDA%3D',
    },
    jaipur: {
      name: 'Jaipur',
      description: 'The Pink City of India, known for its royal palaces, forts, and vibrant culture.',
      background: 'https://wallpapercave.com/wp/wp2395150.jpg', // Updated background URL
    },
    kerala: {
      name: 'Kerala',
      description: 'God’s Own Country, famous for its serene backwaters, lush greenery, and houseboats.',
      background: 'https://images.unsplash.com/photo-1560347876-aeef00ee58a1?w=600&auto=format&fit=crop&q=60',
    },
  };

  return (
    <section
      className="explore"
      style={{
        backgroundImage: `url(${places[activeTab].background})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      <h2>Explore</h2>
      <div className="tabs">
        {Object.keys(places).map((key) => (
          <button
            key={key}
            className={`tab ${activeTab === key ? 'active' : ''}`}
            onClick={() => setActiveTab(key)}
          >
            {places[key].name}
          </button>
        ))}
      </div>
      <div className="tab-content">
        <h3>{places[activeTab].name}</h3>
        <p>{places[activeTab].description}</p>
      </div>
    </section>
  );
}

export default Explore;
